mapper.select:
select u.*, (select d.name from sys_dept d where d.dept_id = u.dept_id) deptName from sys_user u


		and u.`user_id` in (select user_id from sys_user_role where role_id = #{roleId})


		and u.`dept_id` in (select dept_id from sys_dept where dept_id = #{deptIds})



		${searchSql}



	${filterSql}



		order by u.${sidx} ${order}


		order by u.user_id desc



	limit #{offset}, #{limit}

select count(*) from sys_user u


		and u.`user_id` in (select user_id from sys_user_role where role_id = #{roleId})


		and u.`dept_id` in (select dept_id from sys_dept where dept_id = #{deptIds})



		${searchSql}



	${filterSql}

select distinct rm.menu_id from sys_user_role ur 
	LEFT JOIN sys_role_menu rm on ur.role_id = rm.role_id 
where ur.user_id = #{userId}

mapper.delete:
delete from sys_user where user_id in 

	#{userId}

;
delete from sys_user_role where user_id in 

	#{userId}
